import React from 'react';
import Header from '../components/Header';

class Checkout extends React.Component {
  state = {
    cart: JSON.parse(localStorage.getItem('cart')) || [],
    fullname: '',
    cpf: '',
    email: '',
    phone: '',
    cep: '',
    address: '',
    payment: '',
    buttonDisabled: true,
  };

  onChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value }, this.validateButton);
  };

  validateButton = () => {
    const { fullname, email, cpf, phone, cep, address, payment } = this.state;
    const validation = (
      fullname.length > 0
      && cpf.length > 0
      && phone.length > 0
      && cep.length > 0
      && address.length > 0
      && payment !== ''
      && (/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g).test(email)
    //   && email.length > 0
    );
    this.setState({ buttonDisabled: !validation });
  };

  clearCart = () => {
    localStorage.setItem('cart', JSON.stringify([]));
    this.setState({
      cart: [],
      fullname: '',
      cpf: '',
      email: '',
      phone: '',
      cep: '',
      address: '',
      payment: '',
    });
  };

  render() {
    const { cart } = this.state;
    // JSON.parse(localStorage.getItem('cart'));
    const { fullname, email, cpf, phone, cep, address, buttonDisabled } = this.state;
    const totalPrice = cart
      .reduce((acc, curr) => acc + (curr.price * curr.quantity), 0).toFixed(2);

    return (
      <section>
        <Header />

        <section>
          { cart.length === 0
            ? <p data-testid="shopping-cart-empty-message">Seu carrinho está vazio</p>
            : (
              <div className="">
                <p>Revise seus Produtos</p>
                { cart.map((product) => (
                  <div
                    className="card card-side bg-base-100 shadow-xl m-4 w-2/3 jus"
                    key={ product.id }
                    data-testid="product"
                  >
                    <img
                      src={ product.thumbnail }
                      alt={ product.title }
                      className="content-center object-none p-4 w-40"
                    />
                    <div className="card-body">
                      <p
                        data-testid="shopping-cart-product-name"
                        className="card-title"
                      >
                        { product.title }
                      </p>
                      <p>{ `R$ ${(product.price * product.quantity).toFixed(2)}` }</p>
                    </div>
                  </div>
                ))}
                <p>{ `Total: R$ ${totalPrice}` }</p>
              </div>
            )}

        </section>

        <section>
          Informações do Comprador
          <form>
            <label htmlFor="fullname">
              Nome:
              <input
                type="text"
                name="fullname"
                placeholder="Nome Completo"
                data-testid="checkout-fullname"
                value={ fullname }
                onChange={ this.onChange }
              />
            </label>

            <label htmlFor="email">
              Email:
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={ email }
                data-testid="checkout-email"
                onChange={ this.onChange }
              />
            </label>

            <label htmlFor="cpf">
              CPF:
              <input
                type="text"
                name="cpf"
                placeholder="CPF"
                value={ cpf }
                data-testid="checkout-cpf"
                onChange={ this.onChange }
              />
            </label>

            <label htmlFor="phone">
              Telefone:
              <input
                type="text"
                name="phone"
                placeholder="Telefone"
                value={ phone }
                data-testid="checkout-phone"
                onChange={ this.onChange }
              />
            </label>

            <label htmlFor="cep">
              CEP:
              <input
                type="text"
                name="cep"
                placeholder="CEP"
                value={ cep }
                data-testid="checkout-cep"
                onChange={ this.onChange }
              />
            </label>

            <label htmlFor="address">
              Endereço:
              <input
                type="text"
                name="address"
                placeholder="Endereço"
                value={ address }
                data-testid="checkout-address"
                onChange={ this.onChange }
              />
            </label>
          </form>

        </section>

        <section>
          Método de Pagamento
          <label htmlFor="number">
            Boleto
            <input
              type="radio"
              name="payment"
              value="boleto"
              data-testid="ticket-payment"
              onChange={ this.onChange }
            />
          </label>

          <label htmlFor="number">
            Visa
            <input
              type="radio"
              name="payment"
              value="visa"
              data-testid="visa-payment"
              onChange={ this.onChange }
            />
          </label>

          <label htmlFor="number">
            Mastercard
            <input
              type="radio"
              name="payment"
              value="mastercard"
              data-testid="master-payment"
              onChange={ this.onChange }
            />
          </label>

          <label htmlFor="number">
            Elo
            <input
              type="radio"
              name="payment"
              value="elo"
              data-testid="elo-payment"
              onChange={ this.onChange }
            />
          </label>
        </section>

        <button
          data-testid="checkout-btn"
          onClick={ this.clearCart }
          disabled={ buttonDisabled }
        >
          Comprar
        </button>
        {buttonDisabled && <p data-testid="error-msg">Campos inválidos</p>}

      </section>
    );
  }
}

export default Checkout;
