import React, { Component } from 'react';
import { Link } from 'react-router-dom';

export default class ButtonCart extends Component {
  render() {
    const cartQuantity = JSON.parse(localStorage.getItem('cartQuantity'));
    return (
      <section>
        <button type="button">
          <Link
            to="/shopCart"
            data-testid="shopping-cart-button"
          >
            Carrinho

          </Link>
        </button>
         <p data-testid="shopping-cart-size">{ cartQuantity }</p>
      </section>
    );
  }
}
