import React, { Component } from 'react';
import { getProductsFromCategoryAndQuery } from '../services/api';

export default class Card extends Component {
  state = {
    products: [],
    inputValue: '',
  };

  handleInputChange = ({ target}) => {
    this.setState({ inputValue: target.value });
  };

  componentDidMount() {
    this.handleGetProducts();
  }

  handleGetProducts = async (_categoryId, inputValue) => {
    const getProducts = await getProductsFromCategoryAndQuery(_categoryId, inputValue);
    this.setState({ products: getProducts });
    // console.log(products);
  };

  render() {
    const { products } = this.state;
    return (
      <section>
        <input data-testid="query-input" />
        <button
          type="button"
          data-testid="query-button"
          onChange={ this.handleGetProducts }

        >
          Pesquisar

        </button>
        {!products ? 'Nenhum produto foi encontrado'
          : (
            products.map((product) => (
                <div
                    key={ product.id }
                    // name={}
                    // picture={}
                    // price={}
                    data-testid="product"
                >

          </div>))
          )
        }
      </section>

    );
  }
}
