import React, { Component } from 'react';
import ButtonAddCart from '../components/ButtonAddCart';
import Card from '../components/Card';
import ListCategories from '../components/ListCategories';
import Search from '../components/Search';
import { getProductsFromCategoryAndQuery } from '../services/api';

export default class Home extends Component {
  state = {
    clickedValue: '',
  };

  //   getClickedValue = (categoryId) => {
  //     this.setState({ clickedValue: categoryId });
  //   };

  getClickedValue = async ({ target }) => {
    const { id } = target;
    const getCategory = await getProductsFromCategoryAndQuery(id, '');
    this.setState({ clickedValue: getCategory.results });
  };

  render() {
    const { clickedValue } = this.state;
    // console.log(this.state);
    return (
      <section>
        <Search />
        <ListCategories getClickedValue={ this.getClickedValue } />
        <ButtonAddCart />
        <Card clickedValue={ clickedValue } />
      </section>
    );
  }
}
