import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { getProductsFromQuery } from '../services/api';

export default class Card extends Component {
  state = {
    products: {},
    inputValue: '',
  };

  handleInputChange = ({ target }) => {
    this.setState({ inputValue: target.value });
  };

  handleGetProducts = async () => {
    const { inputValue } = this.state;
    const getProducts = await getProductsFromQuery(inputValue);
    this.setState({ products: getProducts });
    // console.log(products);
  };

  render() {
    const { products, inputValue } = this.state;
    const { clickedValue } = this.props;
    console.log(clickedValue);
    return (
      <section>
        <input
          data-testid="query-input"
          value={ inputValue }
          onChange={ this.handleInputChange }

        />
        <button
          type="button"
          data-testid="query-button"
          onClick={ this.handleGetProducts }
        >
          Pesquisar

        </button>
        {products.length === 0 ? <p>Nenhum produto foi encontrado</p>
          : (
            products.filter((product) => clickedValue === product.category_id)
              .map((product) => (
                <div
                  key={ product.id }
                  // title={ product.title }
                  // thumbnail={ product.thumbnail}
                  // price={ product.price }
                  data-testid="product"
                >
                  <p>{product.title}</p>
                  <img
                    src={ product.thumbnail }
                    alt={ product.title }
                  />
                  <p>{product.price}</p>
                </div>)))}
      </section>

    );
  }
}

Card.propTypes = {
  clickedValue: PropTypes.string,
}.isRequired;
