import React, { Component } from 'react';
import PropTypes from 'prop-types';
// import { Link } from 'react-router-dom';
import { getProductById } from '../services/api';
import ButtonCart from '../components/ButtonCart';

export default class Product extends Component {
  state = {
    product: [],
    cart: JSON.parse(localStorage.getItem('cart')) || [],
    email: '',
    text: '',
    buttonDisabled: true,
    rating: '',
  };

  componentDidMount() {
    this.handleGetProductId();
  }

  onChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value }, this.validateButton);
  };

  addCart = (product) => {
    const { cart } = this.state;
    const itens = cart.find((item) => item.id === product.id);
    if (itens) {
      itens.quantity += 1;
    } else {
      cart.push({ ...product, quantity: 1 });
    }
    localStorage.setItem('cart', JSON.stringify(cart));
    this.setState({ cart });
  };

  handleGetProductId = async () => {
    const { match: { params: { id } } } = this.props;
    const data = await getProductById(id);
    // console.log(data);
    this.setState({ product: data });
  };

  validateButton = () => {
    const minChar = 1;
    const { email, rating } = this.state;
    const validation = (rating < minChar
      && (/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g).test(email));
    this.setState({ buttonDisabled: !validation });
  };

  render() {
    const { product, email, text, buttonDisabled } = this.state;
    return (
      <section>
        <p data-testid="product-detail-name">{product.title}</p>
        <img
          data-testid="product-detail-image"
          src={ product.thumbnail }
          alt={ product.title }
        />
        <p data-testid="product-detail-price">{`R$${product.price}`}</p>
        <ButtonCart />
        <button
          type="button"
          data-testid="product-detail-add-to-cart"
          onClick={ () => this.addCart(product) }
        >
          Adiciona ao Carrinho
        </button>

        <section>
          <form action="" method="get">
            <label htmlFor="">
              <input
                type="email"
                data-testid="product-detail-email"
                name="email"
                value={ email }
                placeholder="seu_nome@email.com.br"
                onChange={ this.onChange }
              />
              <input
                type="radio"
                name="rating"
                value="1"
                // id="iRate"
                data-testid="1-rating"
                onChange={ this.onChange }
              />
              <input
                type="radio"
                name="rating"
                value="2"
                // id="iRate"
                data-testid="2-rating"
                onChange={ this.onChange }
              />
              <input
                type="radio"
                name="rating"
                value="3"
                // id="iRate"
                data-testid="3-rating"
                onChange={ this.onChange }
              />
              <input
                type="radio"
                name="rating"
                value="4"
                // id="iRate"
                data-testid="4-rating"
                onChange={ this.onChange }
              />
              <input
                type="radio"
                name="rating"
                value="5"
                // id="iRate"
                data-testid="5-rating"
                onChange={ this.onChange }
              />
              <textarea
                name="text"
                value={ text }
                id=""
                cols="20"
                rows="8"
                data-testid="product-detail-evaluation"
                onChange={ this.onChange }
              >
                Avaliação
              </textarea>
            </label>
            <button
              type="button"
              data-testid="submit-review-btn"
              onClick={ this.validateButton }
              disabled={ buttonDisabled }
            >
              Avaliar
            </button>
            {buttonDisabled && <p data-testid="error-msg">Campos inválidos</p> }

          </form>
        </section>
      </section>
    );
  }
}

Product.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }),
  }).isRequired,
};
