import React, { Component } from 'react';
import { getProductsFromCategoryAndQuery } from '../services/api';

export default class Card extends Component {
  state = {
    products: [],
  };

  componentDidMount() {
    this.handleGetProducts();
  }

  handleGetProducts = async (categoryId, query) => {
    const getProducts = await getProductsFromCategoryAndQuery(categoryId, query);
    this.setState({ products: getProducts });
    console.log(products);
  };

  render() {
    const { products } = this.state;
    return (
      <section>
        <input data-testid="query-input" />
        <button
          type="button"
          data-testid="query-button"
          onChange={ this.handleGetProducts }
        >
          Pesquisar

        </button>
        {!products ? 'Nenhum produto foi encontrado'
          : (
            products.map((product) =>               (
                <div
                    key={ product.id }
                    // name={}
                    // picture={}
                    // price={}
                    data-testid="product"
                >

          </div>))
          )
        }
      </section>

    );
  }
}
