import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { getCategories } from '../services/api';

export default class ListCategories extends Component {
  state = {
    categories: [],
  };

  componentDidMount() {
    this.handleGetCategories();
  }

  handleGetCategories = async ({ target }) => {
    const { id } = target;
    const getCategory = await getProductsFromCategoryAndQuery(id, '');
    this.setState({ categories: getCategory.r });
  };

  render() {
    const { categories } = this.state;
    const { getClickedValue } = this.props;
    console.log(categories);
    return (
      <div>
        {categories.map((category) => (
          <label
            key={ category.id }
            data-testid="category"
            htmlFor={ category.id }
            name="category"
          >
            <input
              type="radio"
              id={ category.id }
              value={ category.id }
              name="category"
              onChange={ getClickedValue }
            />
            { category.name }
          </label>))}
      </div>
    );
  }
}

ListCategories.propTypes = {
  getClickedValue: PropTypes.func,
}.isRequired;

ListCategories.defaultProps = {
  getClickedValue: () => '',
};
