import React, { Component } from 'react';

export default class Checkout extends Component {
  state = {
    fullname: '',
    cpf: '',
    email: '',
    phone: '',
    cep: '',
    address: '',
    buttonDisabled: true,
  };

  onChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value }, this.validateButton);
  };

  validateButton = () => {
    const { fullname, email, cpf, tel, cep, address } = this.state;
    const validation = (
      fullname.length > 0
      && cpf.length > 0
      && tel.length > 0
      && cep.length > 0
      && address.length > 0
      && (/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g).test(email));
    this.setState({ buttonDisabled: !validation });
  };

  clearCart = (product) => {
    const { cart } = this.state;
    const itens = cart.filter((item) => item.id !== product.id);
    console.log(itens);
    // if (itens) {
    //   itens.quantity = 0;
    // } else {
    //   cart.push({ ...product, quantity: 1 });
    // }
    localStorage.setItem('cart', JSON.stringify(itens));
    this.setState({ cart: itens });
  };

  render() {
    const cart = JSON.parse(localStorage.getItem('cart'));
    const { fullname, email, cpf, phone, cep, address, buttonDisabled } = this.state;

    return (
      <section>

        <section>
          <p>Revise seus Produtos</p>
          {cart.map((product) => (
            <div
              key={ product.id }
              data-testid="product"
            >
              <img
                src={ product.thumbnail }
                alt={ product.title }
              />
              <p data-testid="shopping-cart-product-name">{ product.title }</p>
              <p>{ `R$ ${product.price * product.quantity}` }</p>
            </div>
          ))}
          <p>Total: </p>

        </section>

        <section>
          Informações do Comprador
          <form>
            <label htmlFor="fullname">
              Nome:
              <input
                type="text"
                name="fullname"
                value={ fullname }
                data-testid="checkout-fullname"
                onChange={ this.onChange }

              />
            </label>

            <label htmlFor="email">
              Email:
              <input
                type="email"
                name="email"
                value={ email }
                data-testid="checkout-email"
                onChange={ this.onChange }

              />
            </label>

            <label htmlFor="cpf">
              CPF:
              <input
                type="text"
                name="cpf"
                value={ cpf }
                data-testid="checkout-cpf"
                onChange={ this.onChange }

              />
            </label>

            <label htmlFor="phone">
              Telefone:
              <input
                type="text"
                name="phone"
                value={ phone }
                data-testid="checkout-phone"
                onChange={ this.onChange }

              />
            </label>

            <label htmlFor="cep">
              CEP:
              <input
                type="text"
                name="cep"
                value={ cep }
                data-testid="checkout-cep"
                onChange={ this.onChange }

              />
            </label>

            <label htmlFor="address">
              Endereço:
              <input
                type="text"
                name="address"
                value={ address }
                data-testid="checkout-address"
                onChange={ this.onChange }

              />
            </label>
          </form>

        </section>

        <section>
          Método de Pagamento
          <label htmlFor="number">
            <input
              type="radio"
              name="payment"
              value="boleto"
              data-testid="ticket-payment"
              onChange={ this.onChange }
            />
            Boleto
          </label>

          <label htmlFor="number">
            <input
              type="radio"
              name="payment"
              value="visa"
              data-testid="visa-payment"
              onChange={ this.onChange }

            />
            Visa
          </label>

          <label htmlFor="number">
            <input
              type="radio"
              name="payment"
              value="mastercard"
              data-testid="master-payment"
              onChange={ this.onChange }

            />
            Mastercard
          </label>

          <label htmlFor="number">
            <input
              type="radio"
              name="payment"
              value="elo"
              data-testid="elo-payment"
              onChange={ this.onChange }

            />
            Elo
          </label>
        </section>

        <button
          data-testid="checkout-btn"
          onClick={ this.handleClick }
          disabled={ buttonDisabled }
        >
          Comprar

        </button>
        {buttonDisabled && <p data-testid="error-msg">Campos inválidos</p>}

      </section>
    );
  }
}
