import React from 'react';
import Header from '../components/Header';

class Checkout extends React.Component {
  state = {
    cart: JSON.parse(localStorage.getItem('cart')) || [],
    fullname: '',
    cpf: '',
    email: '',
    phone: '',
    cep: '',
    address: '',
    payment: '',
    buttonDisabled: true,
  };

  onChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value }, this.validateButton);
  };

  validateButton = () => {
    const { fullname, email, cpf, phone, cep, address, payment } = this.state;
    const validation = (
      fullname.length > 0
      && cpf.length > 0
      && phone.length > 0
      && cep.length > 0
      && address.length > 0
      && payment !== ''
      && (/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g).test(email)
    //   && email.length > 0
    );
    this.setState({ buttonDisabled: !validation });
  };

  clearCart = () => {
    localStorage.setItem('cart', JSON.stringify([]));
    this.setState({
      cart: [],
      fullname: '',
      cpf: '',
      email: '',
      phone: '',
      cep: '',
      address: '',
      payment: '',
    });
  };

  render() {
    const { cart } = this.state;
    // JSON.parse(localStorage.getItem('cart'));
    const { fullname, email, cpf, phone, cep, address, buttonDisabled } = this.state;
    const totalPrice = cart
      .reduce((acc, curr) => acc + (curr.price * curr.quantity), 0).toFixed(2);

    return (
      <section className="flex flex-col justify-center items-center">
        <Header className="w-96" />

        <section>
          { cart.length === 0
            ? <p data-testid="shopping-cart-empty-message">Seu carrinho está vazio</p>
            : (
              <div className="">
                <p>Revise seus Produtos</p>
                { cart.map((product) => (
                  <div
                    className="card card-side bg-base-100 shadow-xl m-10 "
                    key={ product.id }
                    data-testid="product"
                  >
                    <img
                      src={ product.thumbnail }
                      alt={ product.title }
                      className="content-center object-none p-4 w-40"
                    />
                    <div className="card-body">
                      <p
                        data-testid="shopping-cart-product-name"
                        className="card-title"
                      >
                        { product.title }
                      </p>
                      <p>{ `R$ ${(product.price * product.quantity).toFixed(2)}` }</p>
                    </div>
                  </div>
                ))}
                <p className="text-center text-3xl">{ `Total: R$ ${totalPrice}` }</p>
              </div>
            )}

        </section>

        <section className="flex flex-col m-10">
          Informações do Comprador
          <form className="form-control w-full max-w-xs">
            <label htmlFor="fullname">
              <span className="label-text">Nome</span>
              <input
                className="input input-bordered w-full max-w-xs"
                type="text"
                name="fullname"
                placeholder="Nome Completo"
                data-testid="checkout-fullname"
                value={ fullname }
                onChange={ this.onChange }
              />
            </label>

            <label htmlFor="email">
            <span className="label-text">Email</span>

              Email:
              <input
                className="input input-bordered w-full max-w-xs"
                type="email"
                name="email"
                placeholder="Email"
                value={ email }
                data-testid="checkout-email"
                onChange={ this.onChange }
              />
            </label>

            <label htmlFor="cpf">
            <span className="label-text">CPF</span>

              CPF:
              <input
                className="input input-bordered w-full max-w-xs"
                type="text"
                name="cpf"
                placeholder="CPF"
                value={ cpf }
                data-testid="checkout-cpf"
                onChange={ this.onChange }
              />
            </label>

            <label htmlFor="phone">
            <span className="label-text">Nome</span>

              Telefone:
              <input
                className="input input-bordered w-full max-w-xs"
                type="text"
                name="phone"
                placeholder="Telefone"
                value={ phone }
                data-testid="checkout-phone"
                onChange={ this.onChange }
              />
            </label>

            <label htmlFor="cep">
              CEP:
              <input
                className="input input-bordered w-full max-w-xs"
                type="text"
                name="cep"
                placeholder="CEP"
                value={ cep }
                data-testid="checkout-cep"
                onChange={ this.onChange }
              />
            </label>

            <label htmlFor="address">
              Endereço:
              <input
                className="input input-bordered w-full max-w-xs"
                type="text"
                name="address"
                placeholder="Endereço"
                value={ address }
                data-testid="checkout-address"
                onChange={ this.onChange }
              />
            </label>
          </form>

        </section>

        <section className="join">
          Método de Pagamento
          <input
            className="join-item btn"
            type="radio"
            name="payment"
            value="boleto"
            data-testid="ticket-payment"
            onChange={ this.onChange }
            aria-label="boleto"
          />

          <input
            className="join-item btn"
            type="radio"
            name="payment"
            value="visa"
            data-testid="visa-payment"
            onChange={ this.onChange }
            aria-label="visa"
          />

          <input
            className="join-item btn"
            type="radio"
            name="payment"
            value="mastercard"
            data-testid="master-payment"
            onChange={ this.onChange }
            aria-label="mastercard"
          />

          <input
            className="join-item btn"
            type="radio"
            name="payment"
            value="elo"
            data-testid="elo-payment"
            onChange={ this.onChange }
            aria-label="elo"
          />
        </section>

        <button
          className="btn"
          data-testid="checkout-btn"
          onClick={ this.clearCart }
          disabled={ buttonDisabled }
        >
          Comprar
        </button>
        {buttonDisabled && <p data-testid="error-msg">Campos inválidos</p>}

      </section>
    );
  }
}

export default Checkout;
