import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
// import ButtonCart from '../components/ButtonCart';
import Header from '../components/Header';

export default class ShopCart extends Component {
  state = {
    // listcart: [],
    cart: JSON.parse(localStorage.getItem('cart')) || [],
  };

  // componentDidMount() {
  //   const isEmpty = localStorage.getItem('cart');
  //   if (isEmpty) {
  //     const data = JSON.parse(localStorage.getItem('cart'));
  //     this.setState({ cart: data });
  //   }
  // }

  updateCartQuantity = () => {
    const cart = JSON.parse(localStorage.getItem('cart'));
    const totalQuantity = cart.reduce((acc, curr) => acc + curr.quantity, 0);
    localStorage.setItem('cartQuantity', JSON.stringify(totalQuantity));
  };

  addCart = (product) => {
    const { cart } = this.state;
    const itens = cart.find((item) => item.id === product.id);
    const availabeQuantity = cart
      .find((item) => item.available_quantity === product.available_quantity);
    log
    if (itens.quantity >= availabeQuantity) {
      itens.quantity = availabeQuantity;
    } else if (itens) {
      itens.quantity += 1;
    } else {
      cart.push({ ...product, quantity: 1 });
    }
    localStorage.setItem('cart', JSON.stringify(cart));
    this.setState({ cart });
    this.updateCartQuantity();
  };

  decreaseCart = (product) => {
    const { cart } = this.state;
    const itens = cart.find((item) => item.id === product.id);
    if (itens.quantity <= 1) {
      itens.quantity = 1;
    } else if (itens) {
      itens.quantity -= 1;
    } else {
      cart.push({ ...product, quantity: 1 });
    }
    localStorage.setItem('cart', JSON.stringify(cart));
    this.setState({ cart });
    this.updateCartQuantity();
  };

  removeCart = (product) => {
    const { cart } = this.state;
    const itens = cart.filter((item) => item.id !== product.id);
    console.log(itens);
    // if (itens) {
    //   itens.quantity = 0;
    // } else {
    //   cart.push({ ...product, quantity: 1 });
    // }
    localStorage.setItem('cart', JSON.stringify(itens));
    this.setState({ cart: itens });
    this.updateCartQuantity();
  };

  render() {
    const { cart } = this.state;
    // console.log(cart);
    return (
      <>
        <Header />
        <section>
          {cart.length === 0
            ? <p data-testid="shopping-cart-empty-message">Seu carrinho est√° vazio</p>
            : (
              cart.map((product) => (
                <div
                  key={ product.id }
                  data-testid="product"
                >
                  <p data-testid="shopping-cart-product-name">{product.title}</p>
                  <img
                    src={ product.thumbnail }
                    alt={ product.title }
                  />
                  <p>{`R$${product.price}`}</p>
                  <p data-testid="shopping-cart-product-quantity">{product.quantity}</p>
                  <Link
                    data-testid="product-detail-link"
                    to={ `/product/${product.id}` }
                  >
                    Detalhes

                  </Link>
                  {/* <BtnAdd addCart={ () => this.addCart(product) } /> */}
                  <button
                    type="button"
                    data-testid="product-increase-quantity"
                    onClick={ () => this.addCart(product) }
                  >
                    Adiciona ao Carrinho
                  </button>
                  <button
                    type="button"
                    data-testid="product-decrease-quantity"
                    onClick={ () => this.decreaseCart(product) }
                  >
                    Diminuir do Carrinho
                  </button>
                  <button
                    type="button"
                    data-testid="remove-product"
                    onClick={ () => this.removeCart(product) }
                  >
                    Remover do Carrinho

                  </button>
                </div>)))}
          {/* <ButtonCart /> */}
          <button
            type="button"
          >
            <Link
              data-testid="checkout-products"
              to="/checkout"
            >
              Finalizar
            </Link>

          </button>

        </section>
      </>
    );
  }
}

ShopCart.propTypes = {
  cart: PropTypes.arrayOf(),
}.isRequired;
