import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { getCategories } from '../services/api';

export default class ListCategories extends Component {
  state = {
    categories: [],
    clickedValue: '',
  };

  componentDidMount() {
    this.handleGetCategories();
  }

  handleGetCategories = async () => {
    const getCategory = await getCategories();
    this.setState({ categories: getCategory });
  };

  getClickedValue = (CcategoryId) => {
    this.setState({ clickedValue: target.value });
  };

  // filterProducts = async () => {
  //   const { inputValue } = this.state;
  //   const getProducts = await getProductsFromQuery(inputValue);
  //   this.setState({ products: getProducts.results });
  // };

  render() {
    const { categories } = this.state;
    return (
      <div>
        {categories.map((category) => (
          <label
            key={ category.id }
            data-testid="category"
            htmlFor={ category.id }
            name="category"
          >
            <input
              type="radio"
              id={ category.id }
              value={ category.id }
              name="category"
              onChange={ () => this.getClickedValue(category.id) }
            />
            { category.name }
          </label>))}
      </div>
    );
  }
}

ListCategories.propTypes = {
  clickedValue: PropTypes.string,
}.isRequired;
